      array('id' => '42','trigger'=>'quote-request', 'role_id' => '1','name' => 'New Customer Quote Request','subject' => 'Important- A customer has requested a service quote','slug' => 'New Customer Quote Request','body' => '<p>Hello <span class="mention" data-mention="@admin_company">@admin_company</span> ,</p><p><span class="mention" data-mention="@consumer">@consumer</span> &nbsp;has requested a quote for services</p><p>Start Time: <span class="mention" data-mention="@booking_start_at">@booking_start_at</span>&nbsp;</p><p>End Time: <span class="mention" data-mention="@booking_end_at">@booking_end_at</span>&nbsp;</p><p>Duration: <span class="mention" data-mention="@booking_duration">@booking_duration</span>&nbsp;</p><p>To see more information or to create a quote, log into your Eclipse portal.</p><div class="raw-html-embed"><h3 class="mb-3 mt-0"><a href="@dashboard_url" class="btn btn-primary pull-right">Go to Dashboard</a></h3></div>','type' => NULL,'created_at' => '2021-04-06 21:07:41','updated_at' => '2021-04-06 21:07:41'),
      array('id' => '43','trigger' =>'quote-request','role_id' => '5','name' => 'New Customer Quote Request','subject' => '@admin_company has sent you a service quote!','slug' => 'New Customer Quote Request','body' => '<p>Hello <span class="mention" data-mention="@consumer">@consumer</span> ,</p><p><span class="mention" data-mention="@admin_company">@admin_company</span> has responded to your quote request:</p><p>Start Time: <span class="mention" data-mention="@booking_start_at">@booking_start_at</span>&nbsp;</p><p>End Time: <span class="mention" data-mention="@booking_end_at">@booking_end_at</span>&nbsp;</p><p>Duration: <span class="mention" data-mention="@booking_duration">@booking_duration</span>&nbsp;</p><p>Have a question? Reply to this email. Thanks for choosing <span class="mention" data-mention="@admin_company">@admin_company</span>&nbsp;</p><div class="raw-html-embed"><h3 class="mb-3 mt-0"><a href="@dashboard_url" class="btn btn-primary pull-right">Go to Dashboard</a></h3></div>','type' => NULL,'created_at' => '2021-04-06 21:12:27','updated_at' => '2021-04-06 21:12:27'),

      array('id' => '44','trigger' =>'pending-provider-chat', 'role_id' => '1','name' => 'Pending Provider Chat Waiting in Eclipse','subject' => 'You have an unread provider message waiting in your portal','slug' => 'provider messages admin while they are logged out of their account','body' => '<p>Hello <span class="mention" data-mention="@admin_company">@admin_company</span> ,</p><p><span class="mention" data-mention="@provider">@provider</span> &nbsp;has sent you a message through the Eclipse portal.</p><p>@chat_from_provider&nbsp;</p><p>To read the message and respond, log into your Eclipse portal.&nbsp;</p><div class="raw-html-embed"><h3 class="mb-3 mt-0"><a href="@dashboard_url" class="btn btn-primary pull-right">Go to Dashboard</a></h3></div>','type' => NULL,'created_at' => '2021-04-07 03:00:24','updated_at' => '2021-06-15 04:54:56'),

      array('id' => '45','trigger'=>'pending-admin-chat', 'role_id' => '2','name' => 'Pending Admin Chat Waiting in Eclipse','subject' => 'You have an unread message waiting in your portal','slug' => 'admin messages provider while they are logged out of their account','body' => '<p>Hello <span class="mention" data-mention="@provider">@provider</span> ,</p><p><span class="mention" data-mention="@admin">@admin</span> has sent you a message through the Eclipse portal.</p><p>@chat_from_admin</p><p>To read the message and respond, log into your Eclipse portal.&nbsp;</p><div class="raw-html-embed"><h3 class="mb-3 mt-0"><a href="@dashboard_url" class="btn btn-primary pull-right">Go to Dashboard</a></h3></div>','type' => NULL,'created_at' => '2021-04-07 03:04:23','updated_at' => '2021-06-15 04:56:02'),
      array('id' => '46','trigger'=>'pending-admin-chat', 'role_id' => '5','name' => 'Pending admin Chat Waiting in Eclipse','subject' => 'Important- You have an unread admin message waiting in your portal','slug' => 'Pending admin Chat Waiting in Eclipse','body' => '<p>Hello <span class="mention" data-mention="@consumer">@consumer</span> ,</p><p><span class="mention" data-mention="@admin">@admin</span> &nbsp; has sent you a message through the Eclipse portal.</p><p>@chat_from_admin</p><p>To read the message and respond, log into your Eclipse portal.&nbsp;</p><div class="raw-html-embed"><h3 class="mb-3 mt-0"><a href="@dashboard_url" class="btn btn-primary pull-right">Go to Dashboard</a></h3></div>','type' => NULL,'created_at' => '2021-04-07 03:19:32','updated_at' => '2021-06-15 04:47:53'),

      array('id' => '47','trigger'=>'application-submit', 'role_id' => '1','name' => 'New Provider Application (submitted/received)','subject' => 'Update- You have a new provider application to review','slug' => 'New Provider Application (submitted/received)','body' => '<p>Hello <span class="mention" data-mention="@admin_company">@admin_company</span> ,</p><p><span class="mention" data-mention="@application_name">@application_name</span> has submitted an application.&nbsp;</p><p>To review the application and respond, log into your Eclipse portal</p><div class="raw-html-embed">@dashboard</div>','type' => NULL,'created_at' => '2021-04-07 03:25:21','updated_at' => '2021-06-15 05:03:49'),
      array('id' => '48','trigger'=>'application-submit', 'role_id' => '2','name' => 'New Provider Application (submitted/received)','subject' => 'Application received! @admin_company','slug' => 'A provider submits an application','body' => '<p>Hello <span class="mention" data-mention="@provider">@provider</span> ,</p><p>Thank you for submitting your application for <span class="mention" data-mention="@admin_company">@admin_company</span>&nbsp;</p><p>We have received your documents and will be in touch soon. Question? Just reply to this email.</p><div class="raw-html-embed"><h3 class="mb-3 mt-0"><a href="@dashboard_url" class="btn btn-primary pull-right">Go to Dashboard</a></h3></div>','type' => NULL,'created_at' => '2021-04-07 03:27:41','updated_at' => '2021-04-07 03:27:41'),

      array('id' => '49','trigger'=>'screening-invitation', 'role_id' => '2','name' => 'New Provider Screening Invitation','subject' => '@admin_company has invited you to screen!','slug' => 'When admin approves an application','body' => '<p>Hello <span class="mention" data-mention="@provider">@provider</span> ,</p><p><strong>Next steps:&nbsp;</strong></p><p><strong><span class="mention" data-mention="@admin_company">@admin_company</span>&nbsp;</strong> has reviewed your application and is inviting you to screen. Please follow the link below to complete the screening.&nbsp;</p><p><strong>Be prepared:&nbsp;</strong></p><p>Screenings are best done in a quiet area and with a device that has a strong internet connection and webcam and microphone capabilities.&nbsp;</p><div class="raw-html-embed"><h3 class="mb-3 mt-0"><a style="text-decoration: underline;color:blue;" href="@screening_link" class="btn btn-primary">Screening Link</a></h3>Question? Just reply to this email.</div><p>&nbsp;</p>','type' => NULL,'created_at' => '2021-04-07 03:32:29','updated_at' => '2021-04-07 03:36:04'),

      array('id' => '50','trigger'=>'provider-screening', 'role_id' => '1','name' => 'New Provider Screening','subject' => 'Update- You have a new provider screening to review','slug' => 'New Provider Screening','body' => '<p>Hello <span class="mention" data-mention="@admin_company">@admin_company</span> ,</p><p><span class="mention" data-mention="@provider">@provider</span> &nbsp;has completed their screening.&nbsp;</p><div class="raw-html-embed"><h3 class="mb-3 mt-0"><a href="@show_screening_link" style="text-decoration: underline;color:blue;">Screening </a> </h3></div><p>&nbsp;To review the screening and respond, log into your Eclipse portal.</p><p>&nbsp;</p>','type' => NULL,'created_at' => '2021-04-07 03:53:34','updated_at' => '2021-04-07 03:54:56'),

      array('id' => '51','trigger'=>'application-accepted', 'role_id' => '2','name' => 'Provider Application Accepted','subject' => 'Congratulations! @admin_company','slug' => 'Admin approves applicant and converts to provider','body' => '<p>Hello <span class="mention" data-mention="@provider">@provider</span> ,</p><p>Congratulations! <span class="mention" data-mention="@admin_company">@admin_company</span> &nbsp;has invited you to join our team!&nbsp;</p><p>You will receive a \'Welcome\' email shortly.</p><p>&nbsp;Question? Just reply to this email.</p><p>&nbsp;</p>','type' => NULL,'created_at' => '2021-04-07 04:00:09','updated_at' => '2021-04-07 04:43:55'),

      array('id' => '52','trigger'=>'application-denied', 'role_id' => '2','name' => 'Provider Application Denied','subject' => 'Application Response from @admin_company','slug' => 'Admin denies a provider application or screening','body' => '<p>Hello <span class="mention" data-mention="@provider">@provider</span> ,</p><p>&nbsp;Thank you for your time and interest in joining <span class="mention" data-mention="@admin_company">@admin_company</span> &nbsp;We regret to inform you that we are unable to add you to the team at this time. We wish you the best and will be in touch if anything changes.&nbsp;</p><p>Question? Just reply to this email.</p><p>&nbsp;</p>','type' => NULL,'created_at' => '2021-04-07 04:05:59','updated_at' => '2021-04-07 04:05:59'),

      array('id' => '53','trigger'=>'invoice-issued', 'role_id' => '3','name' => 'New Invoice Issued','subject' => '@admin_company  has issued you a new invoice due @invoice_due_date','slug' => 'Admin or system creates a new customer invoice','body' => '<p>Hello <span class="mention" data-mention="@supervisor">@supervisor</span> ,</p><p><span class="mention" data-mention="@admin_company">@admin_company</span> &nbsp;has issued you a new invoice:&nbsp;</p><p>Invoice #: @invoice_name</p><p>Issue Date: @invoice_issue_date</p><p>Due Date: @invoice_due_date</p><p>Total: @invoice_total</p><p>To review the invoice and submit a payment, log into your <span class="mention" data-mention="@admin_company">@admin_company</span> &nbsp;portal.&nbsp;</p><p><span class="mention" data-mention="@dashboard">@dashboard</span>&nbsp;</p><p>&nbsp;</p>','type' => NULL,'created_at' => '2021-04-07 04:12:37','updated_at' => '2021-04-07 04:14:16'),

      array('id' => '54','trigger'=>'pending-invoice-reminder', 'role_id' => '3','name' => 'Pending Invoice Reminder','subject' => 'Invoice Reminder: @admin_company','slug' => 'Send every 7-days after the invoice is issued to customer','body' => '<p>Hello <span class="mention" data-mention="@supervisor">@supervisor</span> ,</p><p>&nbsp;You have one or more invoice(s) due for payment:&nbsp;</p><p>@multi_invoice_details</p><p>To review the invoices and submit a payment, log into your <span class="mention" data-mention="@admin_company">@admin_company</span> &nbsp;portal.&nbsp;</p><p><span class="mention" data-mention="@dashboard">@dashboard</span>&nbsp;</p>','type' => NULL,'created_at' => '2021-04-07 04:17:44','updated_at' => '2021-04-07 04:19:45'),

      array('id' => '55','trigger'=>'overdue-invoice', 'role_id' => '3','name' => 'Overdue Invoice','subject' => 'Overdue Invoice Alert: @admin_company','slug' => 'customer has an invoice status change to "overdue"','body' => '<p>Hello <span class="mention" data-mention="@supervisor">@supervisor</span> ,</p><p>You have one or more overdue invoice(s) with <span class="mention" data-mention="@admin_company">@admin_company</span> :</p><p>@multi_invoice_details</p><p>To review invoices and submit a payment, log into your <span class="mention" data-mention="@admin_company">@admin_company</span> &nbsp;portal.&nbsp;</p><p><span class="mention" data-mention="@dashboard">@dashboard</span>&nbsp;</p>','type' => NULL,'created_at' => '2021-04-07 04:19:26','updated_at' => '2021-04-07 04:19:26'),

      array('id' => '56','trigger'=>'payment-received', 'role_id' => '1','name' => 'Payment Received','subject' => 'Update- Customer payment received','slug' => 'Customer payment received','body' => '<p>Hello <span class="mention" data-mention="@admin_company">@admin_company</span> ,</p><p><span class="mention" data-mention="@consumer">@consumer</span> &nbsp; has submitted payment for the following assignment(s) or invoice:</p><figure class="table"><table><thead><tr><th>Assignment Number</th><th>Assignment Date</th><th>&nbsp;</th><th>&nbsp;</th></tr></thead><tbody><tr><th><span class="mention" data-mention="@booking_number">@booking_number</span>&nbsp;</th><td><span class="mention" data-mention="@booking_date">@booking_date</span>&nbsp;</td><td>&nbsp;</td><td>&nbsp;</td></tr><tr><th>&nbsp;</th><td>&nbsp;</td><td>&nbsp;</td><td>&nbsp;</td></tr><tr><th>&nbsp;</th><td>&nbsp;</td><td>&nbsp;</td><td>&nbsp;</td></tr></tbody></table></figure><p>&nbsp;</p><p>To review the receipt, log into your Eclipse portal.&nbsp;</p><p><span class="mention" data-mention="@dashboard">@dashboard</span>&nbsp;</p>','type' => NULL,'created_at' => '2021-04-07 04:24:03','updated_at' => '2021-04-07 04:25:21'),

      array('id' => '57','trigger'=>'refund-issued', 'role_id' => '3','name' => 'Refund Issued','subject' => 'Refund Issued: @admin_company','slug' => 'Refund Issued','body' => '<p>Hello <span class="mention" data-mention="@supervisor">@supervisor</span> ,</p><p><span class="mention" data-mention="@admin_company">@admin_company</span> &nbsp;has issued you a refund:&nbsp;</p><p>[Show refund amount and what was refunded (ex: invoice payment, payment hold, etc.)]</p><p>Your refund will be returned to your original payment method or added as credit to your account. To review your account, log into your <span class="mention" data-mention="@admin_company">@admin_company</span> &nbsp;portal.</p><p><span class="mention" data-mention="@dashboard">@dashboard</span>&nbsp;</p>','type' => NULL,'created_at' => '2021-04-07 04:29:25','updated_at' => '2021-04-07 04:29:25'),

