      array('id' => '71','trigger'=>'password-reset', 'role_id' => '5','name' => 'Account Password Reset','subject' => '@admin_company - Password Reset','slug' => 'Account Password Reset','body' => '<p>Hello <span class="mention" data-mention="@provider">@provider</span> ,&nbsp;</p><p>You\'ve requested to reset the password for your Eclipse scheduling portal.</p><div class="raw-html-embed"><h3 class="mb-3 mt-0"><a href="@dashboard_url" class="btn btn-primary ">Reset Password</a></h3></div><p>**Warning: Do not share these credentials with anyone. Report any suspicious account activity to <span class="mention" data-mention="@admin_company">@admin_company</span> &nbsp;immediately.**&nbsp;</p><div class="raw-html-embed"><h3 class="mb-3 mt-0"><a href="@dashboard_url" class="btn btn-primary ">This was not me</a></h3></div><p>To enter the portal, reset your password using the link above and log into your Eclipse account.</p><p><span class="mention" data-mention="@dashboard">@dashboard</span>&nbsp;</p><br><br><br><br><br><p>Question? Just reply to this email.</p>','type' => NULL,'created_at' => '2021-04-09 01:02:01','updated_at' => '2021-04-09 01:02:01'),

      array('id' => '72','trigger'=>'suspicious-activity', 'role_id' => '1','name' => 'Suspicious Account Activity','subject' => 'Important- A user has reported suspicious account activity','slug' => 'sends when a user clicks "Report suspicious activity" in an email or in their portal','body' => '<p>Hello <span class="mention" data-mention="@admin_company">@admin_company</span> ,&nbsp;</p><p>@username has reported suspicious activity in their Eclipse account:</p><p>You\'ve requested to reset the password for your Eclipse scheduling portal.</p><p>[Show what triggered the alert (ex: Clicked "Did not request password reset")]</p><div class="raw-html-embed"><h3 class="mb-3 mt-0"><a href="@dashboard_url" class="btn btn-primary ">Email User</a> <a href="@dashboard_url" class="btn btn-primary ">Unlock User</a> <a href="@dashboard_url" class="btn btn-primary ">View User Profile</a></h3></div><p>To review a user\'s account, log into your Eclipse portal.</p><p><span class="mention" data-mention="@dashboard">@dashboard</span>&nbsp;</p><p>&nbsp;</p>','type' => NULL,'created_at' => '2021-04-09 01:09:06','updated_at' => '2021-04-09 02:36:55'),

      array('id' => '73','trigger'=>'account-lock-notification', 'role_id' => '1','name' => 'Account Lock-out Notification','subject' => 'Important- A user has reported suspicious account activity','slug' => 'sends when a user clicks "Report suspicious activity" in an email or in their portal','body' => '<p>Hello <span class="mention" data-mention="@admin_company">@admin_company</span> ,&nbsp;</p><p>@username has been locked out of their Eclipse account:</p><p>[Show what triggered the lock-out (ex: too many login attempts; user reported suspicious activity)]</p><div class="raw-html-embed"><h3 class="mb-3 mt-0"><a href="@dashboard_url" class="btn btn-primary ">Email User</a> <a href="@dashboard_url" class="btn btn-primary ">Unlock User</a> <a href="@dashboard_url" class="btn btn-primary ">View User Profile</a></h3></div><p>To deactivate the provider, log into your Eclipse account.</p><p><span class="mention" data-mention="@dashboard">@dashboard</span>&nbsp;</p><p>&nbsp;</p>','type' => NULL,'created_at' => '2021-04-09 02:37:08','updated_at' => '2021-04-09 02:37:08'),
