      array('id' => '76','trigger'=>'referral-credit-thank-email', 'role_id' => '2','name' => 'Referral- Thank you email + credit','subject' => 'Thank you for referring- here\'s your credit!','slug' => 'Sends 24-hours after a booking that has a provider\'s referral code','body' => '<p>Hello <span class="mention" data-mention="@provider">@provider</span>,&nbsp;</p><p>Thank you for recommending <span class="mention" data-mention="@admin_company">@admin_company</span> &nbsp;to others!</p><p>As part of our appreciation, a credit for @referral_amount will be added to your payroll.</p><p>Keep spreading the word!</p><p>To review your account, log into your Eclipse portal.</p><p><span class="mention" data-mention="@dashboard">@dashboard</span>&nbsp;</p><p>&nbsp;</p>','type' => NULL,'created_at' => '2021-04-09 02:44:46','updated_at' => '2021-04-09 02:45:43'),
      array('id' => '77','trigger'=>'referral-credit-thank-email', 'role_id' => '5','name' => 'Referral- Thank you email + credit','subject' => 'Thank you for referring- here\'s your credit!','slug' => 'Sends 24-hours after a booking that has a provider\'s referral code','body' => '<p>Hello <span class="mention" data-mention="@provider">@provider</span>,&nbsp;</p><p>Thank you for recommending <span class="mention" data-mention="@admin_company">@admin_company</span> &nbsp;to others!</p><p>As part of our appreciation, a credit for @referral_amount will be added to your payroll.</p><p>Keep spreading the word!</p><p>To review your account, log into your Eclipse portal.</p><p><span class="mention" data-mention="@dashboard">@dashboard</span>&nbsp;</p><p>&nbsp;</p>','type' => NULL,'created_at' => '2021-04-09 02:46:45','updated_at' => '2021-04-09 02:46:45'),

